choose.when:
SELECT count(*) FROM `auth_role_menu` arm LEFT JOIN sys_menu sys on arm.menu_id = sys.id INNER JOIN auth_role ar on ar.id = arm.role_id WHERE 1=1
AND arm.ROLE_ID = #{roleId}
AND ar.code = #{authRoleMenuDetails.code}
AND sys.name like CONCAT('%', #{authRoleMenuDetails.name}, '%')

choose.otherwise:
SELECT count(*) FROM `auth_role_menu` arm LEFT JOIN sys_menu sys on arm.menu_id = sys.id INNER JOIN auth_role ar on ar.id = arm.role_id WHERE 1=1
AND arm.ROLE_ID > #{roleId}
AND ar.code = #{authRoleMenuDetails.code}
AND sys.name like CONCAT('%', #{authRoleMenuDetails.name}, '%')

choose.when:
SELECT arm.role_id ,sys.id,sys.parent_id,sys.name ,sys.url,ar.`CODE` FROM `auth_role_menu` arm LEFT JOIN sys_menu sys on arm.menu_id = sys.id INNER JOIN auth_role ar on ar.id = arm.role_id WHERE 1=1
AND arm.ROLE_ID = #{roleId}
AND ar.code = #{authRoleMenuDetails.code}
AND sys.name like CONCAT('%', #{authRoleMenuDetails.name}, '%')

choose.otherwise:
SELECT arm.role_id ,sys.id,sys.parent_id,sys.name ,sys.url,ar.`CODE` FROM `auth_role_menu` arm LEFT JOIN sys_menu sys on arm.menu_id = sys.id INNER JOIN auth_role ar on ar.id = arm.role_id WHERE 1=1
AND arm.ROLE_ID > #{roleId}
AND ar.code = #{authRoleMenuDetails.code}
AND sys.name like CONCAT('%', #{authRoleMenuDetails.name}, '%')

mapper.update:
update auth_role_menu
    set role_id = #{roleId,jdbcType=INTEGER}
    where menu_id = #{menuId,jdbcType=INTEGER}
